import { ParserOptions } from '@babel/core';

interface Size {
    size: number;
    filename: string;
    source: string;
}

interface Options {
    plugins?: ParserOptions["plugins"];
    filter?: {
        include?: string | RegExp | Array<string | RegExp>;
        exclude?: string | RegExp | Array<string | RegExp>;
    };
    filename?: string;
    rsc?: boolean;
    dev?: "debug" | boolean;
    legacyHmr?: boolean;
    framework?: string;
    source?: string;
    bundleSizes?: Record<string, Array<Size>>;
}

export type { Options as O };
