import { ParserOptions } from '@babel/core';

interface Options {
    plugins?: ParserOptions["plugins"];
    filter?: {
        include?: string | RegExp | Array<string | RegExp>;
        exclude?: string | RegExp | Array<string | RegExp>;
    };
    filename?: string;
    rsc?: boolean;
    dev?: "debug" | boolean;
    legacyHmr?: boolean;
    framework?: string;
    source?: string;
    extra?: boolean;
    isJSX?: boolean;
}

export type { Options as O };
